	use fruithost\Localization\I18N;
	use fruithost\UI\Icon;
	
	$template->header();
?>
    <main class="form-signin w-100 m-auto text-center">
        <form method="post" action="<?php print $template->url(true); ?>">
            <img class="mb-4" src="data:image/png;base64," alt=""/>
            <h1 class="h3 mb-3 fw-normal"><?php I18N::__('Please sign in'); ?></h1>
			<?php
				if(isset($error)) {
					?>
                        <div class="alert alert-danger" role="alert"><?php print $error; ?></div>
					<?php
				}

				if(isset($two_factor) && $two_factor) {
					?>
                    <div class="form-floating">
                        <p><?php I18N::__('Please enter your 2FA code:'); ?></p>
                    </div>
                    <div class="form-floating">
                        <input type="code" class="form-control" id="code" name="code" placeholder="<?php I18N::__('Code'); ?>" autocomplete="off" required autofocus/>
                        <br/>
                        <label for="code"><?php I18N::__('Code'); ?></label>
                    </div>
					<?php
				} else if(isset($two_factor_unknown) && $two_factor_unknown) {
					
				} else {
					?>
                    <div class="form-floating">
                        <input type="text" class="form-control" id="username" name="username" placeholder="<?php I18N::__('Username'); ?>" value="<?php print (isset($username) ? $username : ''); ?>" required autofocus/>
                        <label for="username"><?php I18N::__('Username'); ?></label>
                    </div>
                    <div class="form-floating">
                        <input type="password" class="form-control" id="password" name="password" placeholder="<?php I18N::__('Password'); ?>" required/>
                        <label for="password"><?php I18N::__('Password'); ?></label>
                    </div>

                    <div class="mb-3 mt-3 text-end">
                        <a class="icon-link icon-link-hover" style="--bs-link-hover-color-rgb: 25, 135, 84;" href="<?php print $template->url('/lost-password'); ?>">
							<?php I18N::__('Lost password'); ?>
                            <i class="bi-arrow-right"></i>
                        </a>
                    </div>
					<?php
				}
			?>

            <div class="btn-group btn-group-lg" style="width: 100%;">
				<?php
					if(isset($two_factor_unknown) && $two_factor_unknown) {
						?>
                            <button class="btn btn-primary col-9" name="action" value="back" type="submit"><?php I18N::__('Back'); ?></button>
						<?php
					} else {
						?>
                            <button class="btn btn-primary col-9" name="action" value="login" type="submit"><?php I18N::__('Sign in'); ?></button>
						<?php
					}
				?>
                <button type="button" class="col-3 btn btn-secondary dropdown-toggle dropdown-toggle-split" data-bs-toggle="dropdown" aria-expanded="false">
					<?php Icon::show('language'); ?>
                    <span class="visually-hidden">Toggle Dropdown</span>
                </button>
                <ul class="dropdown-menu dropdown-scroll">
					<?php
						foreach(I18N::getLanguages() as $code => $language) {
							printf('<li><a class="dropdown-item" href="%s">%s</a></li>', $template->url('/login?lang='.$code), $language);
						}
					?>
                </ul>
            </div>
        </form>
    </main>
    <main class="w-200 m-auto text-center">
        <p class="mt-5 mb-3 text-body-secondary">
			<?php
				if($project_copyright) {
					print('<p class="mt-5 mb-3 text-muted">');

                    printf('Powered by <a href="%2$s" target="_blank" class="text-decoration-none" style="color: #c48dbc;">%1$s</a>', 'fruithost', 'https://fruithost.de/');

                    Icon::show('dot');

                    printf('<a href="%2$s" target="_blank" class="icon-link text-decoration-none text-dark-emphasis">%1$s GitHub</a>', Icon::render('github', [
						'classes' => [ 'mb-2' ]
					]), 'https://github.com/fruithost');

					if(defined('DEMO') && DEMO) {
						Icon::show('dot');
						printf('<span class="badge text-bg-danger"><small>%s</small></span>', I18N::get('Demoversion'));
					}

					print('</p>');
				}
			?>
        </p>
    </main>
